    private void jBNuevoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBNuevoActionPerformed
        MateriaData materiaD = new MateriaData();
        Materia materia = new Materia();
        if (!(jTNombre.getText().isEmpty() || jTAño.getText().isEmpty())) {
            try {
                materia.setNombre(jTNombre.getText());
                materia.setAño(Integer.parseInt(jTAño.getText()));
                materia.setEstado(jREstado.isSelected());
                materiaD.guardarMateria(materia);
                borrarFormulario();
            } catch (NumberFormatException ex) {
                JOptionPane.showMessageDialog(this, "en casillero año completar solo con numeros");
            }
        } else {
            JOptionPane.showMessageDialog(this, "DEBE COMPLETAR NOMBRE Y AÑO ");
        }
    }//GEN-LAST:event_jBNuevoActionPerformed

