    private void jBBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBBuscarActionPerformed
        MateriaData materiaD = new MateriaData();
        Materia materia = new Materia();
        try {
            materia = materiaD.buscarMateria(Integer.parseInt(jTCodigo.getText()));
            jTNombre.setText(materia.getNombre());
            jTAño.setText(materia.getAño() + "");
            jREstado.setSelected(materia.isEstado());
        } catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(this, "debe ingresar solo numeros en el campo CODIGO ");
        }
    }//GEN-LAST:event_jBBuscarActionPerformed

    private void jBEliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBEliminarActionPerformed
        MateriaData materiaD = new MateriaData();
        Materia materia = new Materia();
       jBBuscarActionPerformed(evt);
        materia = materiaD.buscarMateria(Integer.parseInt(jTCodigo.getText()));
        materiaD.eliminarMateria(materia.getIdMateria());
    }//GEN-LAST:event_jBEliminarActionPerformed


